using WebApplication1.Clases;

namespace WebApplication1.Interfaces
{
    public class IAuthService
    {

        private readonly Notflix _notflix;

        public IAuthService()
        {
            _notflix = new Notflix();
        }

        public bool Login(string correoElectronico, string contrae単a)
        {
            return _notflix.Login(correoElectronico, contrae単a);
        }

        public List<Usuario> GetUsuarios()
        {
            return _notflix.GetUsuarios();
        }
        public List<Cuenta> GetCuenta()
        {
            return _notflix.GetCuenta();
        }

        public void Logout()
        {
            _notflix.Logout();
        }

        public bool IsAuthenticated()
        {
            try
            {
                _notflix.GetUsuarios();
                return true;
            }
            catch(UnauthorizedAccessException)
            {
                return false;
            }
        }

        public bool Regist(string correoElectronico, string contrae単a)
        {
            return _notflix.Regist(correoElectronico, contrae単a);
        }

        public List<Pelicula> GetPeliculas()
        {
            Pelicula p1 = new Pelicula("data:image/jpeg;base64,", "lol", 120, 5);
            _notflix.AddPelicula(p1);
            return _notflix.GetPeliculas();
        }

        public List<Serie> GetSeries()
        {
            return _notflix.GetSeries();
        }

        public List<Juego> GetJuegos()
        {
            return _notflix.GetJuegos();
        }
    }

}
